INSERT INTO `carrito_arma` (`id`, `arm_nombre`, `arm_precio`, `arm_origen`, `arm_calibre`, `arm_capacidad`, `arm_peso`, `arm_velocidad`, `arm_tiemporecarga`, `arm_catergoria_id`, `arm_descripcion`, `arm_url_imagen`, `arm_valoracion`, `arm_foto`) VALUES (1, 'Glock-18', 400, 'Austria', '9mm Parabellum', 20, 900, 'N/A', 3, 1, 'Es posible utilizarla en modo semiautomatico,que va disparando balas rapidamente con potencia baja, o la llamarada, que dispara 3 balas al mismo tiempo, pero más lento. Precisión media, potencia escasa, útil en distancias cortas o medias, pero con un cargador de 20 balas que la de una gran ventaja frente a otras pistolas de menor capacidad.', 'url', 1, NULL);
INSERT INTO `carrito_arma` (`id`, `arm_nombre`, `arm_precio`, `arm_origen`, `arm_calibre`, `arm_capacidad`, `arm_peso`, `arm_velocidad`, `arm_tiemporecarga`, `arm_catergoria_id`, `arm_descripcion`, `arm_url_imagen`, `arm_valoracion`, `arm_foto`) VALUES (2, 'USP-S', 500, 'Alemania', '.45 ACP', 12, 1000, 'N/A', 3, 1, 'La USP es un arma bastante ligera. Mientras se ejersa, la velocidad del jugador el movimiento es de 250 unidades por segundo, lo que significa que no sufre reducción de velocidad. La USP es una de las muchas armas capaces de disparar bajo el agua.', 'url', 2, NULL);
INSERT INTO `carrito_arma` (`id`, `arm_nombre`, `arm_precio`, `arm_origen`, `arm_calibre`, `arm_capacidad`, `arm_peso`, `arm_velocidad`, `arm_tiemporecarga`, `arm_catergoria_id`, `arm_descripcion`, `arm_url_imagen`, `arm_valoracion`, `arm_foto`) VALUES (3, '228 Compact', 600, 'Suiza, Alemania', '.357 SIG', 13, 1030, 'N/A', 5, 1, 'a SIG P228 es una pistola semiautomática de doble acción fabricada por la empresa suizo-alemana SIG-Sauer (Schweizerische Industrie Gesellschaft y J. P. Sauer & Sohn). Es la versión compacta de la pistola SIG P226.', 'url', 2, NULL);
INSERT INTO `carrito_arma` (`id`, `arm_nombre`, `arm_precio`, `arm_origen`, `arm_calibre`, `arm_capacidad`, `arm_peso`, `arm_velocidad`, `arm_tiemporecarga`, `arm_catergoria_id`, `arm_descripcion`, `arm_url_imagen`, `arm_valoracion`, `arm_foto`) VALUES (4, 'Desert Eagle', 650, 'Israel', '.50 Action Express', 7, 1800, 'N/A', 2, 1, 'La Desert Eagle es una pistola semiautomática de grueso calibre accionada por los gases del disparo, diseñada por la firma estadounidense Magnum Research y fabricada principalmente en Israel por IMI (Industrias Militares de Israel), ahora Israel Weapons Industry. ', 'url', 4, NULL);
INSERT INTO `carrito_arma` (`id`, `arm_nombre`, `arm_precio`, `arm_origen`, `arm_calibre`, `arm_capacidad`, `arm_peso`, `arm_velocidad`, `arm_tiemporecarga`, `arm_catergoria_id`, `arm_descripcion`, `arm_url_imagen`, `arm_valoracion`, `arm_foto`) VALUES (5, 'Five-SeveN', 750, 'Bégica', '5.7x28mm', 20, 618, 'N/A', 5, 1, 'La Five-Seven es una débil pistola semi-automática disponible sólo para los Anti-Terroristas. Es cara, a pesar de que no hace mucho daño. Su única ventaja es su capacidad para perforar el blindaje, es muy precisa, incluso a grandes distancias, y 20 balas darán ventaja frente a otras pistolas. A pesar de estas razones, La Five-Seven es una de las pistolas menos populares de Counter-Strike.', 'url', 2, NULL);
INSERT INTO `carrito_arma` (`id`, `arm_nombre`, `arm_precio`, `arm_origen`, `arm_calibre`, `arm_capacidad`, `arm_peso`, `arm_velocidad`, `arm_tiemporecarga`, `arm_catergoria_id`, `arm_descripcion`, `arm_url_imagen`, `arm_valoracion`, `arm_foto`) VALUES (6, 'Dual Berettas', 800, 'Italia', '9mm', 30, 1150, 'N/A', 5, 1, 'Las Elites son moderadamente potentes pistolas semi-automáticas disponibles sólo para los Terroristas. Son usualmente compradas por su estética atractiva o diversión. Son una de las pocas armas que se añadieron a causa del abrumador apoyo de los aficionados. Por esta razón, las Elites son una de las pistolas más populares de Counter-Strike.', 'url', 2, NULL);
INSERT INTO `carrito_arma` (`id`, `arm_nombre`, `arm_precio`, `arm_origen`, `arm_calibre`, `arm_capacidad`, `arm_peso`, `arm_velocidad`, `arm_tiemporecarga`, `arm_catergoria_id`, `arm_descripcion`, `arm_url_imagen`, `arm_valoracion`, `arm_foto`) VALUES (7, 'Leone 12 Gauge Super', 1700, 'Italia', '12', 8, 3500, 'N/A', 8, 2, 'La M3 es muy eficaz en el combate de cerca y se ve comúnmente en Office y Nuke. Debido a que ambas escopetas son bastante similares, se utilizan las dos. Por estas razones, La M3 es un arma popular, aunque no es comparable a la AWSM, M4A1, AK-47, o la Night Hawk .50c.', 'url', 2, NULL);
INSERT INTO `carrito_arma` (`id`, `arm_nombre`, `arm_precio`, `arm_origen`, `arm_calibre`, `arm_capacidad`, `arm_peso`, `arm_velocidad`, `arm_tiemporecarga`, `arm_catergoria_id`, `arm_descripcion`, `arm_url_imagen`, `arm_valoracion`, `arm_foto`) VALUES (8, 'XM1014', 3000, 'Italia', '12', 7, 4000, '400RPM', 6, 2, ' La YG1265 es muy eficaz en el combate de cerca y se ve comúnmente en Office y Nuke. No hay un ganador claro si es entre la YG1265 o la M3r, por lo tanto, se utilizan las dos. Por estas razones, la YG12654 es un arma popular, aunque no es comparable a la AWSM, M4A1, AK-47, o el Desert Eagle. Es capaz de disparar un tiro después de cada apriete del gatillo. En Counter-Strike, esto se traduce en disparar continuamente si el disparador se mantiene pulsado.Debido a esto, la M1014 es considerada en ocasiones una arma vencedora, pero debido a su corto alcance no está en el mismo nivel que otro tipo de armas.', 'url', 3, NULL);
INSERT INTO `carrito_arma` (`id`, `arm_nombre`, `arm_precio`, `arm_origen`, `arm_calibre`, `arm_capacidad`, `arm_peso`, `arm_velocidad`, `arm_tiemporecarga`, `arm_catergoria_id`, `arm_descripcion`, `arm_url_imagen`, `arm_valoracion`, `arm_foto`) VALUES (9, 'Schmidt Machine Pistol', 1250, 'Austria', '9mm Parabellum', 30, 1300, '857RPM', 2, 3, 'El TMP es un subfusil bastante débil sólo está disponible para los Anti-Terroristas. La mayoría de los jugadores tienden a no usarla debido a su bajo daño y mira inexacta. Por estas razones, la TMP es rara, por lo general se sustituye por la MP5 o P90. ', 'url', 2, NULL);
INSERT INTO `carrito_arma` (`id`, `arm_nombre`, `arm_precio`, `arm_origen`, `arm_calibre`, `arm_capacidad`, `arm_peso`, `arm_velocidad`, `arm_tiemporecarga`, `arm_catergoria_id`, `arm_descripcion`, `arm_url_imagen`, `arm_valoracion`, `arm_foto`) VALUES (10, 'MAC-10', 1400, 'Estados Unidos', '.45 ACP', 30, 3820, '857RPM', 3, 3, 'La MAC-10 es un Micro Subfúsil moderadamente potente disponible sólo para los Terroristas. Las principales desventajas de esta arma son su baja precisión y el alto retroceso que tiene. Por estas razones, La MAC-10 es muy poco común, y muy rara se ve durante el juego.', 'url', 2, NULL);
INSERT INTO `carrito_arma` (`id`, `arm_nombre`, `arm_precio`, `arm_origen`, `arm_calibre`, `arm_capacidad`, `arm_peso`, `arm_velocidad`, `arm_tiemporecarga`, `arm_catergoria_id`, `arm_descripcion`, `arm_url_imagen`, `arm_valoracion`, `arm_foto`) VALUES (11, 'KM Sub-Machine Gun', 1500, 'Alemania', '9mm', 30, 3420, '800RPM', 3, 3, 'La MP5 es un subfusil moderadamente potente del mercado para ambos equipos. Su único inconveniente es su bajo daño. Tiene la tasa más rápidas de fuego mas que la SMG, en segundo lugar la P90, y la SMG es más precisa, aunque eso no la hace una gran diferencia. Por estas razones, La MP5 es muy común, posiblemente el arma más utilizada, además de la CV-47, Desert Eagle, y la M4A1.', 'url', 4, NULL);
INSERT INTO `carrito_arma` (`id`, `arm_nombre`, `arm_precio`, `arm_origen`, `arm_calibre`, `arm_capacidad`, `arm_peso`, `arm_velocidad`, `arm_tiemporecarga`, `arm_catergoria_id`, `arm_descripcion`, `arm_url_imagen`, `arm_valoracion`, `arm_foto`) VALUES (12, 'UMP-45', 1700, 'Alemania', '.45 ACP', 25, 2270, '600RPM', 4, 3, 'La UMP45 es una sub-ametrallador moderada a disposición de ambos equipos. La mayoría de los jugadores tienden a no usarla debido a su fuego bajo-medio y baja penetración de los daños. También tiene la tasa más lenta de fuego de todas las SMG. Por estas razones, el UMP45 es muy poco común, por lo general se sustituye por el MP5 o P90.', 'url', 2, NULL);
